// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.6;

/// Shared public library for on-chain NFT functions
interface IPublicSharedMetadata {
  /// @param unencoded bytes to base64-encode
  function base64Encode(bytes memory unencoded) external pure returns (string memory);

  /// Encodes the argument json bytes into base64-data uri format
  /// @param json Raw json to base64 and turn into a data-uri
  function encodeMetadataJSON(bytes memory json) external pure returns (string memory);

  /// Proxy to openzeppelin's toString function
  /// @param value number to return as a string
  function numberToString(uint256 value) external pure returns (string memory);
}

contracts/erc721Static.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.6;

import { ERC721Upgradeable } from '@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol';
import { IERC2981Upgradeable, IERC165Upgradeable } from '@openzeppelin/contracts-upgradeable/interfaces/IERC2981Upgradeable.sol';
import { OwnableUpgradeable } from '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import { CountersUpgradeable } from '@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol';
import { AddressUpgradeable } from '@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol';

import { SharedNFTLogic } from './SharedNFTLogic.sol';
import { IEditionSingleMintable } from './IEditionSingleMintable.sol';

contract erc721Static is ERC721Upgradeable, IEditionSingleMintable, IERC2981Upgradeable, OwnableUpgradeable {
  using CountersUpgradeable for CountersUpgradeable.Counter;
  event PriceChanged(uint256 amount);
  event EditionSold(uint256 price, address owner);
  
  string public description;
  string public animationUrl;
  string public imageUrl;
  uint256 public editionSize;
  // Current token id minted
  CountersUpgradeable.Counter private atEditionId;
  // Royalty amount in bps
  uint256 royaltyBPS;
  mapping(address => bool) allowedMinters;
  uint256 public salePrice;
  address payable[] public players;

  // NFT rendering logic contract
  SharedNFTLogic private immutable sharedNFTLogic;

  constructor( SharedNFTLogic _sharedNFTLogic) {
    sharedNFTLogic = _sharedNFTLogic;
    
  }

  function initialize(
    address _owner,
    string memory _name,
    string memory _symbol,
    string memory _description,
    string memory _animationUrl,
    string memory _imageUrl,
    uint256 _editionSize,
    uint256 _royaltyBPS
  ) public initializer {
    __ERC721_init(_name, _symbol);
    __Ownable_init();
    // Set ownership to original sender of contract call
    transferOwnership(_owner);
    description = _description;
    animationUrl = _animationUrl;
    imageUrl = _imageUrl;
    editionSize = _editionSize;
    royaltyBPS = _royaltyBPS;
    // Set edition id start to be 1 not 0
    atEditionId.increment();
  }

  /// @dev returns the number of minted tokens within the edition
  function totalSupply() public view returns (uint256) {
    return atEditionId.current() - 1;
  }


 // return balance in wei
    function getBalance() public view returns(uint){
        return address(this).balance;
    }
    // return prize balance
     function getPrize() public view returns(uint){
        return address(this).balance * 80 / 100;
    }

    function boothFee() public view returns(uint) {
        return address(this).balance * 20 / 100;
    }

    // random integer function
    function random() internal view returns(uint){
       return uint(keccak256(abi.encodePacked(block.difficulty, block.timestamp, players.length)));
    }

    // require contract owner
    // require at least 3 players
    // Call random integer function
    // transferring 80% of contract balance to the winner
    // use player index to compute random winner
    // resetting the lottery for the next round
    function pickWinner() public onlyOwner{
        require (players.length >= 3);
        require (totalSupply() >= 12);
        uint r = random();
        address payable winner;
        uint index = r % 12;
        winner = players[index]; 
        winner.transfer(getPrize());
        players = new address payable[](0);
        withdraw();
        //emit WinnerPicked(winner);
    }


  /**
        Simple eth-based sales function
        More complex sales functions can be implemented through ISingleEditionMintable interface
     */

  /**
      @dev This allows the user to purchase a edition edition
           at the given price in the contract.
     */
  function purchase() external payable returns (uint256) {
    require(salePrice > 0, 'Not for sale');
    require(msg.value == salePrice, 'Wrong price');
    address[] memory toMint = new address[](1);
    toMint[0] = msg.sender;
    players.push(payable(msg.sender));
    emit EditionSold(salePrice, msg.sender);
    return _mintEditions(toMint);
  }

  /**
      @param _salePrice if sale price is 0 sale is stopped, otherwise that amount 
                       of ETH is needed to start the sale.
      @dev This sets a simple ETH sales price
           Setting a sales price allows users to mint the edition until it sells out.
           For more granular sales, use an external sales contract.
     */
  function setSalePrice(uint256 _salePrice) external onlyOwner {
    salePrice = _salePrice;
    emit PriceChanged(salePrice);
  }

  /**
      @dev This withdraws ETH from the contract to the contract owner.
     */
  function withdraw() internal onlyOwner {
    // No need for gas limit to trusted address.
    AddressUpgradeable.sendValue(payable(owner()), address(this).balance * 20 / 100);
  }

  /**
      @dev This helper function checks if the msg.sender is allowed to mint the
            given edition id.
     */
  function _isAllowedToMint() internal view returns (bool) {
    if (owner() == msg.sender) {
      return true;
    }
    if (allowedMinters[address(0x0)]) {
      return true;
    }
    return allowedMinters[msg.sender];
  }

  /**
      @param to address to send the newly minted edition to
      @dev This mints one edition to the given address by an allowed minter on the edition instance.
     */
  function mintEdition(address to) external override returns (uint256) {
    require(_isAllowedToMint(), 'Needs to be an allowed minter');
    address[] memory toMint = new address[](1);
    toMint[0] = to;
    return _mintEditions(toMint);
  }

  /**
      @param recipients list of addresses to send the newly minted editions to
      @dev This mints multiple editions to the given list of addresses.
     */
  function mintEditions(address[] memory recipients) external override returns (uint256) {
    require(_isAllowedToMint(), 'Needs to be an allowed minter');
    return _mintEditions(recipients);
  }

  /**
        Simple override for owner interface.
     */
  function owner() public view override(OwnableUpgradeable, IEditionSingleMintable) returns (address) {
    return super.owner();
  }

  /**
      @param minter address to set approved minting status for
      @param allowed boolean if that address is allowed to mint
      @dev Sets the approved minting status of the given address.
           This requires that msg.sender is the owner of the given edition id.
           If the ZeroAddress (address(0x0)) is set as a minter,
             anyone will be allowed to mint.
           This setup is similar to setApprovalForAll in the ERC721 spec.
     */
  function setApprovedMinter(address minter, bool allowed) public onlyOwner {
    allowedMinters[minter] = allowed;
  }

  /**
      @dev Allows for updates of edition urls by the owner of the edition.
           Only URLs can be updated (data-uris are supported), hashes cannot be updated.
     */
  function updateEditionURLs(string memory _imageUrl, string memory _animationUrl) public onlyOwner {
    imageUrl = _imageUrl;
    animationUrl = _animationUrl;
  }

  /// Returns the number of editions allowed to mint (max_uint256 when open edition)
  function numberCanMint() public view override returns (uint256) {
    // Return max int if open edition
    if (editionSize == 0) {
      return type(uint256).max;
    }
    // atEditionId is one-indexed hence the need to remove one here
    return editionSize + 1 - atEditionId.current();
  }

  /**
        @param tokenId Token ID to burn
        User burn function for token id 
     */
  function burn(uint256 tokenId) public {
    require(_isApprovedOrOwner(_msgSender(), tokenId), 'Not approved');
    _burn(tokenId);
  }

  /**
      @dev Private function to mint als without any access checks.
           Called by the public edition minting functions.
     */
  function _mintEditions(address[] memory recipients) internal returns (uint256) {
    uint256 startAt = atEditionId.current();
    uint256 endAt = startAt + recipients.length - 1;
    require(editionSize == 0 || endAt <= editionSize, 'Sold out');
    while (atEditionId.current() <= endAt) {
      _mint(recipients[atEditionId.current() - startAt], atEditionId.current());
      atEditionId.increment();
    }
    return atEditionId.current();
  }

  /**
      @dev Get URIs for edition NFT
      @return imageUrl,  animationUrl
     */
  function getURIs() public view returns (string memory, string memory) {
    return (imageUrl, animationUrl);
  }

  /**
        @dev Get royalty information for token
        @param _salePrice Sale price for the token
     */
  function royaltyInfo(uint256, uint256 _salePrice) external view override returns (address receiver, uint256 royaltyAmount) {
    if (owner() == address(0x0)) {
      return (owner(), 0);
    }
    return (owner(), (_salePrice * royaltyBPS) / 10_000);
  }

  /**
        @dev Get URI for given token id
        @param tokenId token id to get uri for
        @return base64-encoded json metadata object
    */
  function tokenURI(uint256 tokenId) public view override returns (string memory) {
    require(_exists(tokenId), 'No token');

    return sharedNFTLogic.createMetadataEdition(name(), description, imageUrl, animationUrl, tokenId, editionSize);
  }

  function supportsInterface(bytes4 interfaceId) public view override(ERC721Upgradeable, IERC165Upgradeable) returns (bool) {
    return type(IERC2981Upgradeable).interfaceId == interfaceId || ERC721Upgradeable.supportsInterface(interfaceId);
  }
}

contracts/sFactory.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.6;

import { ClonesUpgradeable } from '@openzeppelin/contracts-upgradeable/proxy/ClonesUpgradeable.sol';
import { CountersUpgradeable } from '@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol';

import './erc721Static.sol';

contract sFactory {
  using CountersUpgradeable for CountersUpgradeable.Counter;

  /// Counter for current contract id upgraded
  CountersUpgradeable.Counter private atContract;

  /// Address for implementation of erc721Static to clone
  address public implementation;

  /// Initializes factory with address of implementation logic
  constructor(address _implementation) {
    implementation = _implementation;
  }

  function createEdition(
    string memory _name,
    string memory _symbol,
    string memory _description,
    string memory _animationUrl,
    string memory _imageUrl,
    uint256 _editionSize,
    uint256 _royaltyBPS
  ) external returns (uint256) {
    uint256 newId = atContract.current();
    address newContract = ClonesUpgradeable.cloneDeterministic(implementation, bytes32(abi.encodePacked(newId)));
    erc721Static(newContract).initialize(msg.sender, _name, _symbol, _description, _animationUrl, _imageUrl, _editionSize, _royaltyBPS);
    emit CreatedEdition(newId, msg.sender, _editionSize, newContract);
    // Returns the ID of the recently created minting contract
    // Also increments for the next contract creation call
    atContract.increment();
    return newId;
  }

  function getEditionAtId(uint256 editionId) external view returns (erc721Static) {
    return erc721Static(ClonesUpgradeable.predictDeterministicAddress(implementation, bytes32(abi.encodePacked(editionId)), address(this)));
  }

  event CreatedEdition(uint256 indexed editionId, address indexed creator, uint256 editionSize, address editionContractAddress);
}

contracts/raffleFactory.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.6;

import {ClonesUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/ClonesUpgradeable.sol";
import {CountersUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";

import "./erc721Static.sol";

contract raffleFactory {
    using CountersUpgradeable for CountersUpgradeable.Counter;

    /// Counter for current contract id upgraded
    CountersUpgradeable.Counter private atContract;

    /// Address for implementation of erc721Static to clone
    address public implementation;
            string  _name = "The Buny Project";
        string  _symbol = "rBUNY";
        string  _description = "The Buny Project";
        string  _animationUrl = "https://ipfs.io/ipfs/Qmd94YsrsK5jPbi675XiwyLwMhcM6HS6MULC7mqQGMnXtW";
        string  _imageUrl  = "https://ipfs.io/ipfs/Qmd94YsrsK5jPbi675XiwyLwMhcM6HS6MULC7mqQGMnXtW";
        uint256 _raffleSize = 12;
        uint256 _royaltyBPS = 500;


    /// Initializes factory with address of implementation logic
    constructor(address _implementation) {
        implementation = _implementation;

    }

   
    function createRaffle(

    ) external returns (uint256) {
        uint256 newId = atContract.current();
        address newContract = ClonesUpgradeable.cloneDeterministic(
            implementation,
            bytes32(abi.encodePacked(newId))
        );
        erc721Static(newContract).initialize(
            msg.sender,
            _name,
            _symbol,
            _description,
            _animationUrl,
            _imageUrl,
            _raffleSize,
            _royaltyBPS
        );
        emit CreatedRaffle(newId, msg.sender, _raffleSize, newContract);
        // Returns the ID of the recently created minting contract
        // Also increments for the next contract creation call
        atContract.increment();
        return newId;
    }


    function getRaffleAtId(uint256 raffleId)
        external
        view
        returns (erc721Static)
    {
        return
            erc721Static(
                ClonesUpgradeable.predictDeterministicAddress(
                    implementation,
                    bytes32(abi.encodePacked(raffleId)),
                    address(this)
                )
            );
    }


    event CreatedRaffle(
        uint256 indexed raffleId,
        address indexed creator,
        uint256 raffleSize,
        address raffleContractAddress
    );
}
